module ApplicationHelper
    def user_avatar(user)
        img_url = user.user_image
        img = image_tag(img_url ? img_url : 'user-default.jpg', class: "rounded mx-auto d-block shadow",  src: 'Image Not Found', 
        onerror: 'this.error=null;this.src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/271deea8-e28c-41a3-aaf5-2913f5f48be6/de7834s-6515bd40-8b2c-4dc6-a843-5ac1a95a8b55.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..BopkDn1ptIwbmcKHdAOlYHyAOOACXW0Zfgbs0-6BY-E"')
    end
end
